package techmentTrainingDay2;

public class JaggedArray {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int arr[][] = new int[4][];
		arr[0] =new int[3];
		arr[1] =new int[4];
		arr[2] =new int[7];
		arr[3] =new int[5];
	for(int j=0;j< arr.length; j++) {
		for(int i=0;i< arr[i].length; i++) {
			 System.out.println(arr[i][j]);
		}
	}
	}

}
